import './Hero.css';
import imageEmi from '../images/cicero-emiliano.jpeg';

function Hero () {
    return (
        <div class="hero">
            <div>
                <h1>Hey there!<br />My name is Emiliano, but everyone calls me Emi.</h1>
                <p>I'm a Sr. Product Designer based in Madrid and Specialized in <b>Visual Design</b>, <b>Design Systems</b>, and <b>Design Libraries</b></p>
            </div>
            <img src={imageEmi} class="image" />
        </div>
    )
}

export default Hero;
